//实例化服务器应用
let app = express()

//实现静态服务器
app.use(express.static('static'))

//自定义接口

app.get('/api/userList', (req, res) => {
    //请求的信息：req对象
    //响应的操作和信息：res对象
    res.json({
        state: "ok",
